	function get_options($set) {
	global $mysqli;
	$options = array();
	$query = $mysqli->query("SELECT * FROM ss_options WHERE option_set='$set' ORDER BY id ASC");
	while ($row = $query->fetch_assoc()) {
		$options[$row["option_name"]] = $row["option_value"];
	}  
	return $options;
	}
	function set_options($data,$set) {
	unset($data['save']);
	global $mysqli;
	foreach ($data AS $key=>$value) {
	$check = $mysqli->query("SELECT option_name FROM ss_options WHERE option_name='$key'");
	$value = $mysqli->real_escape_string(htmlspecialchars($value,ENT_QUOTES));	
	if ($check->num_rows == 0) {
	$excute = $mysqli->query("INSERT INTO ss_options (option_name,option_value,option_default,option_set) VALUES ('$key','$value','$value','$set')");
	} else {
	$excute = $mysqli->query("UPDATE ss_options SET option_value='$value' WHERE option_name='$key'");
	}
	if ($excute) {
	$message = notification('success','All Changes Saved.');
	} else {
	$message = notification('danger','Error Happened.'.$mysqli->error);
	}
	}
	return $message;
	}
	if (isset($_POST['save'])) {
	$message = set_options($_POST,'General');
	}
